import React from 'react'
import { Header, HeaderBtnBot, HeaderWrap } from './headerStyle'
import { calendarData, DateItem } from './calendar'
import { Location } from './location'

class header extends React.Component {
    constructor() {
        super()
        this.state = {
            lunch: "lunch active",
            dinner: "dinner",
            btnBotStatus: "active",
            locStatus: false,
            theposition: 0,
            calendar: calendarData,
        }
    }

    // componentDidMount() {
    //     window.addEventListener('scroll', this.listenToScroll)
    // }

    // componentWillUnmount() {
    //     window.removeEventListener('scroll', this.listenToScroll)
    // }

    // listenToScroll = () => {
    //     const winScroll =
    //         document.body.scrollTop || document.documentElement.scrollTop
    //     const scrolled = winScroll

    //     if (this.state.theposition < scrolled) {
    //         this.setState({
    //             btnBotStatus: "notActive",
    //             theposition: scrolled,
    //         })
    //     } else {
    //         this.setState({
    //             btnBotStatus: "active",
    //             theposition: scrolled,
    //         })
    //     }
    // }

    check(date) {
        alert('halo ' + date)
    }


    btnLunch = () => {
        return (
            this.setState({
                lunch: "lunch active",
                dinner: "dinner"
            })
        )
    }

    btnDinner = () => {
        return (
            this.setState({
                lunch: "lunch",
                dinner: "dinner active"
            })
        )
    }

    btnLocation = () => {
        console.log(this.state.locStatus)
    }

    render() {
        const dateItems = this.state.calendar.map(item => <DateItem onclick={this.check} key={item.date} item={item} />)
        return (
            <div>
                <Location active={this.state.locStatus} />
                <HeaderWrap>
                    <Header>

                        <div className="headerNav">
                            <button>
                                <span className="material-icons"> arrow_back</span>
                            </button>
                            <div className="navSideRight">
                                <div className="upTxt">
                                    <p>Alamat Pengantaran</p>
                                </div>
                                <button onclick={this.btnLocation} className="botTxt">
                                    <p>Tokopedia Tower</p>
                                    <span className="material-icons"> keyboard_arrow_down</span>
                                </button>
                            </div>
                        </div>

                        <div className="headerDate">
                            {dateItems}
                        </div>

                    </Header>


                    <HeaderBtnBot className={this.state.btnBotStatus}>
                        <button onClick={this.btnLunch} className={this.state.lunch}>Lunch</button>
                        <button onClick={this.btnDinner} className={this.state.dinner}>Dinner</button>
                    </HeaderBtnBot>
                </HeaderWrap>
            </div>
        )
    }
}

export default header
