import React, { useEffect } from 'react'
import { Header, HeaderBtnBot, HeaderWrap } from './headerStyle'

class header extends React.Component {
    constructor() {
        super()
        this.state = {
            lunch: "lunch active",
            dinner: "dinner"
        }
    }

    componentDidMount() {
        document.addEventListener('scroll', this.trackScrolling);
    }

    componentWillUnmount() {
        document.removeEventListener('scroll', this.trackScrolling);
    }

    nav = React.createRef();

    handleScroll = () => {
      lastScrollY = window.scrollY;

      if (!ticking) {
        window.requestAnimationFrame(() => {
          this.nav.current.style.top = `${lastScrollY}px`;
          ticking = false;
        });

        ticking = true;
      }
    };

    dateItem = (props) => {
        return (
            <button className="dateItem" active={props} >
                <span className="day">SEN</span>
                <span className="date">10</span>
            </button>
        )
    }

    btnLunch = () => {
        return (
            this.setState({
                lunch: "lunch active",
                dinner: "dinner"
            })
        )
    }

    btnDinner = () => {
        return (
            this.setState({
                lunch: "lunch",
                dinner: "dinner active"
            })
        )
    }

    render() {
        return (
            <HeaderWrap>
                <Header>
                    <div className="headerNav">
                        <button>
                            <span className="material-icons"> arrow_back</span>
                        </button>
                        <div className="navSideRight">
                            <div className="upTxt">
                                <p>Alamat Pengantaran</p>
                            </div>
                            <button className="botTxt">
                                <p>Tokopedia Tower</p>
                                <span className="material-icons"> keyboard_arrow_down</span>
                            </button>
                        </div>
                    </div>

                    <div className="headerDate">

                        {this.dateItem("true")}
                        {this.dateItem()}
