import styled from 'styled-components'
import * as appStyle from '../appStyle'

export const Header = styled.div`
    display : flex;
    flex-direction : column;
    padding : 0 8px;

    .headerNav{
        display : flex;
        flex-direction : row;
        margin : 8px 0;
        color : ${appStyle.mainText}

        button{
            border: none;
            background-color : transparent;
            outline: none;
        }

        .navSideRight{
            display : flex;
            flex-direction : column;
            margin-left : 16px;

            p { 
                margin :0;
            }

            .upTxt{
                font-size : 12px;
                text-transform : uppercase;
            }

            .botTxt{
                display : flex;
                flex-direction : row;
                align-items :center;
                justify-content : center;

                p{
                    margin-top : 4px;
                    font-size : 20px;
                    font-weight : 500;
                }

                span{
                    color : ${appStyle.addCart};
                    font-size : 20px;
                }

            }
        }
    }


`
