import React, { useEffect } from 'react'
import { Header, HeaderBtnBot, HeaderWrap } from './headerStyle'

class header extends React.Component {
    constructor() {
        super()
        this.state = {
            lunch: "lunch active",
            dinner: "dinner",
            theposition: window.pageYOffset
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.listenToScroll)
      }

      componentWillUnmount() {
        window.removeEventListener('scroll', this.listenToScroll)
      }

    listenToScroll = () => {
        const winScroll =
          document.body.scrollTop || document.documentElement.scrollTop

        const height =
          document.documentElement.scrollHeight -
          document.documentElement.clientHeight

        const scrolled = winScroll

        this.setState({
          theposition: scrolled,
        })
        console.log(this.state.theposition)
      }

    dateItem = (props) => {
        return (
            <button className="dateItem" active={props} >
                <span className="day">SEN</span>
                <span className="date">10</span>
            </button>
        )
    }

    btnLunch = () => {
        return (
            this.setState({
                lunch: "lunch active",
                dinner: "dinner"
            })
        )
    }

    btnDinner = () => {
        return (
            this.setState({
                lunch: "lunch",
                dinner: "dinner active"
            })
        )
    }

    render() {
        return (
            <HeaderWrap>
                <Header>
                    <div className="headerNav">
                        <button>
                            <span className="material-icons"> arrow_back</span>
                        </button>
                        <div className="navSideRight">
                            <div className="upTxt">
                                <p>Alamat Pengantaran</p>
                            </div>
                            <button className="botTxt">
                                <p>Tokopedia Tower</p>
                                <span className="material-icons"> keyboard_arrow_down</span>
                            </button>
                        </div>
                    </div>

                    <div className="headerDate">

                        {this.dateItem("true")}
                        {this.dateItem()}
