import styled from 'styled-components'

export const Header = styled.div`
    display : flex;
    flex-direction : column;
    padding : 0 8px;

    .headerNav{
        display : flex;
        flex-direction : row;


        button{
            border: none;
            background-color : transparent;
            outline: none;
        }

        .navSideRight{
            display : flex;
            flex-direction : column;
            margin-left : 16px;

            p { 
                margin :0;
            }

            .upTxt{
                font-size : 12px;
                text-transform : uppercase;
            }

            .botTxt{
                display : flex;
                flex-direction : row;
                align-items :center;
                justify-content : center;
            }
        }
    }


`
